'use strict'

const PIXI = (typeof window !== 'undefined') ? window.PIXI || require('pixi.js') : {}

class TextureExtractor {
  constructor ({
    width,
    height,
    tilewidth,
    tileheight,
    tileset,
    offset,
    count,
    scaleMode
  }) {
    this.width = width
    this.height = height
    this.tilewidth = tilewidth
    this.tileheight = tileheight
    this.tileset = (typeof tileset === 'string') ? PIXI.Texture.from(tileset) : tileset
    this.offset = offset || 0
    this.textureCache = {}
    this.scaleMode = scaleMode || PIXI.SCALE_MODES.NEAREST
    if (count) {
      for (let i = 0; i < count; i++) {
        this.prepareTexture(i)
      }
    }
  }
  prepareTexture (frame) {
    const width = (this.width / this.tilewidth)
    const x = ((frame - this.offset) % width) * this.tilewidth
    const y = Math.floor((frame - this.offset) / width) * this.tileheight
    const rect = new PIXI.Rectangle(x, y, this.tilewidth, this.tileheight)

    this.textureCache[frame] = new PIXI.Texture(this.tileset, rect)
    this.textureCache[frame].baseTexture.scaleMode = this.scaleMode
    this.textureCache[frame].cacheAsBitmap = true
  }
  getFrame (frame) {
    if (!this.textureCache[frame]) {
      this.prepareTexture(frame)
    }
    return this.textureCache[frame]
  }
}

exports.default = TextureExtractor

'use strict'

const FMath = require('fmath')
const fmath = new FMath()
const gameloop = require('gameloop-compatible')

// for universal time processing functions
const IS_BACKEND = (typeof window === 'undefined')

// next cycle run universal function
const nextCycle = IS_BACKEND ? setImmediate : setTimeout

// get current time in nano seconds universal
const getTime = IS_BACKEND ? getTimeNode :
  ((typeof performance !== 'undefined') ? getTimeModernBrowser : getTimeBrowser)

class Game {
  constructor (interval = 16) {
    this.levels = []
    this.loopId = gameloop.setGameLoop(this.loop.bind(this), interval)
    this.interval = interval
  }
  tickLevel (level) {
    level.tick()
  }
  loop () {
    this.levels.forEach(this.tickLevel.bind(this))
  }
}

class Level {
  constructor (props = {}) {
    this.heros = props.heros || {}
    this.blocks = props.blocks || {}
  }
  each (set, callback) {
    Object.keys(set).forEach((id) => callback(set[id]))
  }
  eachHero (callback) {
    this.each(this.heros, callback)
  }
  tick () {
    console.log('tick @ level')
  }
  toArray () {
    const array = [[]]
    this.eachHero((hero) => array[0].push(hero.toArray()))
    return array
  }
  removeHero (hero) {
    if (this.onRemoveHero) {
      this.onRemoveHero(hero)
    }
    this.heros[hero.id] = null
    delete this.heros[hero.id]
  }
  fromArray (array = [], HeroClass = Hero) {
    this.eachHero((hero) => {
      if (!array[0].find((heroArray) => heroArray[HERO_ID] === hero.id)) {
        this.removeHero(hero)
      }
    })
    array[0].forEach((heroArray) => {
      const id = heroArray[HERO_ID]
      if (this.heros[id]) {
        this.updateHero(id, heroArray)
      } else {
        this.addHero(heroArray, HeroClass)
      }
    })
    if (this.fromArrayExtension) {
      this.fromArrayExtension(array)
    }
  }
  updateHero (id, heroArray = []) {
    this.heros[id].fromArray(heroArray)
    if (this.onUpdateHero) {
      this.onUpdateHero(this.heros[id])
    }
  }
  addHero (heroArray = [], HeroClass = Hero) {
    const id = heroArray[HERO_ID] || randomId()
    this.heros[id] = new HeroClass({ id })
    this.heros[id].x = heroArray[HERO_X] || this.width * random()
    this.heros[id].y = heroArray[HERO_Y] || 0
    this.heros[id].fromArray(heroArray)
    if (this.onCreateHero) {
      this.onCreateHero(this.heros[id])
    }
    return this.heros[id]
  }
  fromTiled (tiled = {}) {
    this.width = tiled.width
    this.height = tiled.height
    this.blocks = {}
    this.stops = {}
    this.tileset = {
      tilewidth: tiled.tilesets[0].tilewidth,
      imagewidth: tiled.tilesets[0].imagewidth
    }
    for (let y = 0; y < this.height; y++) {
      for (let x = 0; x < this.width; x++) {
        const pos = y * this.width + x
        const offset = `${x}:${y}`
        this.blocks[offset] = [
          tiled.layers[0].data[pos],
          tiled.layers[1].data[pos]
        ]
        this.stops[offset] = tiled.layers[2].data[pos]
      }
    }
  }
}

class Hero {
  constructor ({ id = randomId() }) {
    this.id = id
    this.x = undefined
    this.y = undefined
  }
  toArray () {
    return [
      this.id,
      this.x,
      this.y
    ]
  }
  fromArray (array = [], force = false) {
    if (force) {
      this.id = array[HERO_ID]
    }
    if (force || (this.id === array[HERO_ID])) {
      this.move(array[HERO_X], array[HERO_Y])
    }
  }
  move (x, y) {
    this.x = x
    this.y = y
  }
}

// time specific constants
const s2nano = 1e9
const s2ms = 1e3
const nano2s = 1 / s2nano

// gameframework-lite specific
const HERO_ID = 0
const HERO_X = 1
const HERO_Y = 2

// optimisations
const HALF_PI = Math.PI / 2

// returns in ms
function getTimeNode () {
  var hrtime = process.hrtime()
  return (hrtime[0] + hrtime[1] * nano2s) * s2ms
}

// returns in ms
function getTimeModernBrowser () {
  return performance.now()
}

// returns in ms
function getTimeBrowser () {
  return Date.now()
}

function atan2 (y, x) {
  if (x > 0) {
    return fmath.atan(y / x)
  }
  if (x < 0) {
    if (y >= 0) {
      return fmath.atan(y / x) + Math.PI
    }
    return fmath.atan(y / x) - Math.PI
  }
  if (x === 0) {
    if (y === 0) {
      return undefined
    }
    if (y > 0) {
      return HALF_PI
    }
    if (y < 0) {
      return -HALF_PI
    }
  }
}

function random () {
  return Math.floor(Math.random() * 1001) / 1000
}

function randomId () {
  return Math.random().toString(36).slice(2, 7)
}

exports.getTime = getTime
exports.nextCycle = nextCycle
exports.Game = Game
exports.Level = Level
exports.Hero = Hero
exports.sin = fmath.sin.bind(fmath)
exports.cos = fmath.cos.bind(fmath)
exports.atan2 = atan2
exports.random = random
exports.randomId = randomId

(function () {
'use strict';

// special thanks to
// http://buildnewgames.com/box2dweb/
// wrapped up by me Jacek Pietal <prozi85@gmail.com>

const Box2D = require('box2d-es6');

// box2d
const b2Vec2 = Box2D.Common.Math.b2Vec2;
const b2BodyDef = Box2D.Dynamics.b2BodyDef;
const b2Body = Box2D.Dynamics.b2Body;
const b2FixtureDef = Box2D.Dynamics.b2FixtureDef;
const b2Fixture = Box2D.Dynamics.b2Fixture;
const b2World = Box2D.Dynamics.b2World;
const b2MassData = Box2D.Collision.Shapes.b2MassData;
const b2PolygonShape = Box2D.Collision.Shapes.b2PolygonShape;
const b2CircleShape = Box2D.Collision.Shapes.b2CircleShape;
const b2DebugDraw = Box2D.Dynamics.b2DebugDraw;	

const GRAVITY = 9.8;
const BODIES_SLEEP = true;

class Physics {
	constructor ({ scale = 30, gravity = GRAVITY }) {
		this.gravity = new b2Vec2(0, gravity);
		this.world = new b2World(this.gravity, BODIES_SLEEP);
		this.scale = scale;
		this.dtRemaining = 0;
		this.stepAmount = 1 / 60;
	}
	step (dt = 0) {
		this.dtRemaining += dt;
		while (this.dtRemaining > this.stepAmount) {
			this.dtRemaining -= this.stepAmount;
			this.world.Step(this.stepAmount,
			8, // velocity iterations
			3); // position iterations
		}
		if (this.debugDraw) {
			this.world.DrawDebugData();
		}
	}
	debug (context) {
		if (context) {
			this.debugDraw = new b2DebugDraw();
			this.debugDraw.SetSprite(context);
			this.debugDraw.SetDrawScale(this.scale);
			this.debugDraw.SetFillAlpha(0.3);
			this.debugDraw.SetLineThickness(1.0);
			this.debugDraw.SetFlags(b2DebugDraw.e_shapeBit | b2DebugDraw.e_jointBit);
			this.world.SetDebugDraw(this.debugDraw);
		}
	};
};

const BODY_DEFAULTS = {
	shape: "block",
	width: 1,
	height: 1,
	radius: 0.5
};

const BODY_FIXTURE_DEFAULTS = {
	density: 2,
	friction: 1,
	restitution: 0.2
};

const BODY_DEFINITION_DEFAULTS = {
	active: true,
	allowSleep: true,
	angle: 0,
	angularVelocity: 0,
	awake: true,
	bullet: false,
	fixedRotation: false
};

class Body {
	constructor (physics, details = {}) {

		// Create the definition
		const definition = new b2BodyDef();

		// Set up the definition
		for (let k in BODY_DEFINITION_DEFAULTS) {
			definition[k] = details[k] || BODY_DEFINITION_DEFAULTS[k];
		}
		definition.position = new b2Vec2(details.x || 0, details.y || 0);
		definition.linearVelocity = new b2Vec2(details.vx || 0, details.vy || 0);
		definition.userData = {};
		definition.type = details.type == "static" ? b2Body.b2_staticBody : b2Body.b2_dynamicBody;

		// Create the Body
		const body = physics.world.CreateBody(definition);

		// Create the fixture
		const fixtureDef = new b2FixtureDef();
		for (let l in BODY_FIXTURE_DEFAULTS) {
			fixtureDef[l] = details[l] || BODY_FIXTURE_DEFAULTS[l];
		}

		details.shape = details.shape || BODY_DEFAULTS.shape;

		switch (details.shape) {
			case "circle":
				details.radius = details.radius || BODY_DEFAULTS.radius;
				fixtureDef.shape = new b2CircleShape(details.radius);
				break;
			case "polygon":
				fixtureDef.shape = new b2PolygonShape();
				fixtureDef.shape.SetAsArray(details.points, details.points.length);
				break;
			case "block":
			default:
				details.width = details.width || BODY_DEFAULTS.width;
				details.height = details.height || BODY_DEFAULTS.height;

				fixtureDef.shape = new b2PolygonShape();
				fixtureDef.shape.SetAsBox(
					details.width / 2,
					details.height / 2
				);
				break;
		}

		body.CreateFixture(fixtureDef);

		return body;
	}
};

if (typeof module !== 'undefined') {
	module.exports = {
		Physics,
		Body,
		b2Vec2
	};
}

})();

'use strict'

const { Level } = require('./index')
const PIXI = (typeof window !== 'undefined') ? window.PIXI || require('pixi.js') : {}

class View {
  constructor (screenSize = 480, anchor = document.body) {
    this.screenSize = screenSize
    this.pixi = new PIXI.Application(window.innerWidth, window.innerHeight, {
      autoResize: false,
      roundPixels: true,
      clearBeforeRender: false,
      resolution: 1,
      autoStart: false
    })
    this.camera = new PIXI.Container()
    this.pixi.stage.addChild(this.camera)
    this.level = new Level()
    this.level.onUpdateHero = this._onUpdateHero.bind(this)
    this.level.onCreateHero = this._onCreateHero.bind(this)
    this.level.onRemoveHero = this._onRemoveHero.bind(this)
    anchor.appendChild(this.pixi.view)
    window.addEventListener('resize', this.onResize.bind(this), true)
    this.onResize()
  }
  get hero () {
    return this.level.heros[this.id]
  }
  cameraToHero (hero) {
    this.camera.x = Math.floor(-hero.sprite.x + window.innerWidth / 2 / this.scale)
    this.camera.y = Math.floor(-hero.sprite.y + window.innerHeight / 2 / this.scale)
  }
  onUpdateHero (hero) {
    // this is what you should override
  }
  _onUpdateHero (hero) {
    this.onUpdateHero(hero)
    if (hero.sprite) {
      hero.sprite.x = hero.x * this.level.accuracy
      hero.sprite.y = hero.y * this.level.accuracy
    }
  }
  // this is what you should override
  onCreateHero (hero) {
    hero.sprite = PIXI.Sprite.from('bunny.png')
    hero.sprite.anchor.set(0.5)
    hero.sprite.texture.baseTexture.scaleMode = PIXI.SCALE_MODES.NEAREST
  }
  _onCreateHero (hero) {
    if (!hero.sprite) {
      this.onCreateHero(hero)
      if (hero.sprite) {
        const layer = (this.layers ? this.layers.heros : this.camera)
        hero.sprite.x = hero.x * this.level.accuracy
        hero.sprite.y = hero.y * this.level.accuracy
        layer.addChild(hero.sprite)
      }
    }
  }
  onRemoveHero (hero) {
    // this is what you should override
  }
  _onRemoveHero (hero) {
    this.onRemoveHero(hero)
    if (hero.sprite && hero.sprite.parent) {
      hero.sprite.parent.removeChild(hero.sprite)
    }
  }
  onResize () {
    this.scale = this.getScale()
    this.pixi.renderer.resize(window.innerWidth, window.innerHeight)
    this.pixi.stage.scale.set(this.scale)
  }
  getScale () {
    return +(Math.sqrt(window.innerWidth * window.innerHeight) / this.screenSize).toFixed(2)
  }
  getTexture (frame) {
    if (!this.textureCache[frame]) {
      // <-- tiled firstgid === 1
      const size = this.level.tileset.tilewidth
      const width = (this.level.tileset.imagewidth / this.level.tileset.tilewidth)
      const x = ((frame - 1) % width) * size
      const y = Math.floor((frame - 1) / width) * size
      this.textureCache[frame] = new PIXI.Texture(
        this.tileset, new PIXI.Rectangle(x, y, size, size)
      )
      this.textureCache[frame].baseTexture.scaleMode = PIXI.SCALE_MODES.NEAREST
    }
    return this.textureCache[frame]
  }
  createSprite (frame, nearest = PIXI.SCALE_MODES.NEAREST) {
    let sprite
    if (isFinite(frame)) {
      sprite = new PIXI.Sprite(this.getTexture(parseInt(frame, 10)))
    } else {
      sprite = PIXI.Sprite.from(frame)
    }
    sprite.texture.baseTexture.scaleMode = nearest
    return sprite
  }
  addBlock (x, y, block, layer = this.layers.blocks, nearest = PIXI.SCALE_MODES.NEAREST) {
    const sprite = this.createSprite(block, nearest)
    sprite.width = sprite.height = this.level.tileset.tilewidth
    sprite.x = Math.floor(this.level.tileset.tilewidth * x)
    sprite.y = Math.floor(this.level.tileset.tilewidth * y)
    sprite.anchor.set(0.5)
    layer.addChild(sprite)
  }
  addBlocks () {
    for (let y = 0; y < this.level.height; y++) {
      for (let x = 0; x < this.level.width; x++) {
        const block = this.level.blocks[`${x}:${y}`]
        if (block && Array.isArray(block)) {
          for (let i = 0; i < block.length; i++) {
            if (parseInt(block[i], 10)) {
              this.addBlock(x, y, block[i])
            }
          }
        }
      }
    }
  }
  createLevel (tiled) {
    this.textureCache = {}
    this.layers = {
      blocks: new PIXI.Container(),
      heros: new PIXI.Container()
    }
    this.tileset = PIXI.Texture.from(tiled.tilesets[0].image)
    this.tileset.scaleMode = PIXI.SCALE_MODES.NEAREST;
    this.camera.addChild(this.layers.blocks)
    this.camera.addChild(this.layers.heros)
    this.level.fromTiled(tiled)
    this.addBlocks()
  }
  _onMouseMove (event) {
    event.stopPropagation()
    const e = event.touches ? event.touches[0] : event
    this.mouse = {
      x: e.clientX,
      y: e.clientY
    }
    if (this.onMouseMove) {
      this.onMouseMove()
    }
  }
  _onMouseClick (event) {
    this._onMouseMove(event)
    if (this.onMouseClick) {
      this.onMouseClick()
    }
  }
  useMouse (disableContextMenu = false) {
    window.addEventListener('touchmove', this._onMouseMove.bind(this), false)
    window.addEventListener('touchstart', this._onMouseClick.bind(this), false)
    window.addEventListener('mousemove', this._onMouseMove.bind(this), false)
    window.addEventListener('mousedown', this._onMouseClick.bind(this), false)
    if (disableContextMenu) {
      this.disableContextMenu()
    }
  }
  disableContextMenu () {
    document.body.addEventListener('contextmenu', (event) => event.preventDefault(), false)
  }
}

exports.default = View

